import { useState, useEffect } from "react";
import { parseEther, formatEther } from '@ethersproject/units';
import Web3 from "web3"
import Election from './contracts/Election.json';
import {Route, Routes, Router} from 'react-router-dom';
import LandingPage from "./LandingPage"


function App(): React.ReactNode {
  const ElectionContractAddress = "0x1c140031D85621bEf4Bf35F9679B6D72ffb87b1e";
  const emptyAddress = '0x0000000000000000000000000000000000000000';
  const [account, setAccount] = useState(null);
  useEffect(() => {
    const init = async () => {
      try {
        const web3 = await getWeb3();
        const accounts = await web3.eth.getAccounts();
        const electionContract = await getContract(web3);


        setAccount(accounts[0]);

      } catch (error) {
        console.error(error);
      }
    };

    init();
  }, []);

  const getWeb3 = async () => {
    if (typeof window.ethereum !== "undefined") {
      const provider = window.ethereum;
      await provider.enable();
      return new Web3(provider);
    } else {
      return new Web3(new Web3.providers.HttpProvider("http://localhost:7545"));
    }
  };

  const getContract = async (web3) => {
    const contract = TruffleContract(Election);
    contract.setProvider(web3.currentProvider);
    return contract.deployed();
  };


  return (

   <div>
      <Routes>
      <Route exact path ="/" element={<LandingPage/>}/>

    </Routes>
   </div>
  );
}

export default App;
