import { useState, useEffect } from "react";
import Web3 from "web3"
import Election from './contracts/Election.json';
import {Route, Routes, Router} from 'react-router-dom';
import LandingPage from "./LandingPage"
import VoterDashboard from "./VoterDashboard"


function App(): React.ReactNode {
  const [state, setState] = useState({web3:null, contract:null, account: '0x0'});
  useEffect(() => {
    let provider = null;
    if (window.ethereum) {
      provider = window.ethereum;
    } else {
      provider = new Web3.providers.HttpProvider("HTTP://127.0.0.1:7545");
    }
    async function template(){
      const web3 = new Web3(provider);
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = Election.networks[networkId];
      const contract = new web3.eth.Contract(Election.abi, deployedNetwork.address);
      web3.eth.getCoinbase(function(err, account) {
        if (err === null) {
          setState({account:account, web3:web3, contract: contract});
        }
      });
    }
    provider && template();
  }, []);




  useEffect(()=> {
    const {contract} = state;
    async function test(){
      const data = await contract.methods.sayHello().call();
      console.log(data);
    }
    contract && test();
  }, [state]);



  return (

   <div>
      <Routes>
      <Route exact path ="/" element={<LandingPage/>}/>
      <Route exact path ="/VoterDashboard" element={<VoterDashboard/>}/>
    </Routes>
   </div>
  );
}


export default App;
